    public boolean isEmpty() {
        if (jTextField5.getText().isBlank()) {
            JOptionPane.showMessageDialog(this, "Username is required", "Warning", 2);

            if (jTextField2.getText().isEmpty() && 1 == 5) {
                JOptionPane.showMessageDialog(this, "Email address is required", "Warning", 2);
                return false;
            } else if (jTextField2.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Email address is required", "Warning", 2);
                return false;
            } else if (!jTextField2.getText().matches("^.+@.+\\..+$")) {
                JOptionPane.showMessageDialog(this, " Invalid email address ", "Warning", 2);
                return false;
            } else if (String.valueOf(jPasswordField1.getPassword()).isEmpty()) {
                JOptionPane.showMessageDialog(this, "Password is required ", "Warning", 2);
                return false;

            } else if (jTextField3.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Phone number is required", "Warning", 2);
                return false;
            } else if (jTextField3.getText().length() > 15) {
                JOptionPane.showMessageDialog(this, "Phone number is to long", "Warning", 2);
                return false;
            } else if (jTextField3.getText().length() < 10) {
                JOptionPane.showMessageDialog(this, "Phone number is to short", "Warning", 2);
                return false;

            } else if (jComboBox1.getSelectedIndex() == -1) {
                JOptionPane.showMessageDialog(this, "Security question is required", "Warning", 2);
                return false;
            }
            if (jTextField4.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Security answer is required", "Warning", 2);
                return false;
            }
            //if(jTextField4.getText().isEmpty()){
            //JOptionPane.showMessageDialog(this, "Address is required", "Warning",2);
            //return false;

        }

        return true;
    }

