    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        for (double i = 0.1; i <= 1.0; i += 0.1) {
            String s = "" + i;
            float f = Float.parseFloat(s);
            this.setOpacity(f);
            try {
                Thread.sleep(40);
            } catch (InterruptedException ex) {
                Logger.getLogger(LogIn.class.getName()).log(Level.SEVERE, null, ex);
            }

        }
    }//GEN-LAST:event_formWindowOpened

    private void btnSignUpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSignUpActionPerformed
        if (isEmpty()) {
            int id = Integer.parseInt(jTextField1.getText());
            String username = jTextField5.getText();
            String email = jTextField2.getText();
            String password = String.valueOf(jPasswordField1.getPassword()).toString();
            String phone = jTextField3.getText();
            String secqus = jComboBox1.getSelectedItem().toString();
            String ans = jTextField4.getText();
            //String address = 
            if (!user.isEmailExist(email)) {
                if (!user.isPhoneExist(phone)) {
                   user.insert(id, username, email, password, phone, secqus, ans);
                   new LogIn().setVisible(true);
                   this.dispose();
                } else {
                    JOptionPane.showMessageDialog(this, "This phone number already exists", "Warning", 2);
                }
            } else {
                JOptionPane.showMessageDialog(this, "This email address already exists", "Warning", 2);

            }

        }
    }//GEN-LAST:event_btnSignUpActionPerformed

